css:
@font-face {
  font-family: 'Redacted';
  font-weight: normal;
  font-style: normal;
  src: url(atom://minimap/resources/redacted-fixed.woff) format('woff');
  unicode-range: U+21-FF;
}
.pane.with-minimap :not(.minimap-editor) > .editor {
  padding-right: 10%;
}
.pane.with-minimap .vertical-scrollbar {
  right: 10%;
}
.minimap-on-left .pane.with-minimap :not(.minimap-editor) > .editor {
  padding-right: 0;
  padding-left: 10%;
}
.minimap-on-left .pane.with-minimap .vertical-scrollbar {
  right: 0;
}
.minimap-on-left .pane.with-minimap .minimap {
  left: initial;
  right: 90%;
}
.minimap {
  position: absolute;
  left: 90%;
  width: 10%;
  bottom: 0;
  overflow: hidden;
  box-sizing: border-box;
}
.minimap .editor {
  padding: 0 !important;
}
.minimap .lines {
  word-spacing: -1px;
}
.minimap .line {
  opacity: .6;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
.minimap .minimap-scroller {
  position: absolute;
  display: none;
  right: 0;
  width: 3px;
  min-height: 2px;
  z-index: 10;
  background: #4182c4;
}
.minimap .minimap-scroller.visible {
  display: block;
}
.minimap .minimap-wrapper {
  position: absolute;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
  -webkit-transform-origin: 0 0;
}
.minimap .minimap-visible-area {
  position: absolute;
  width: 100%;
  background-color: #888;
  opacity: .2;
}
.minimap .minimap-editor {
  position: absolute;
  cursor: default;
  width: 100%;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
.minimap[data-theme="redacted"] .editor {
  font-family: Redacted, Inconsolata, Monaco, Consolas, 'Courier New', Courier !important;
  font-weight: normal !important;
}
.minimap .accelerated {
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
